function min(arr) {

    let n = 0;

    let arr_ = arr.join("");
    for (let i = 0; i < arr_.length; i++) {
        if (arr_[i] === "-")
            n++;
    }

    while (n) {
        arr_ = arr_.replace('-', '1');
        n--;
    }

    if (Number.isNaN(Number(arr_)))
        return "Wrong input";

    let min_ = arr[0];  

    for (let i = 1; i < arr.length; i++) {
        if (arr[i] < min)
            min_ = arr[i];
    }        

    return min_;
}

module.exports = { min };