function summ(arr) {

    let n = 0;

    let arr_ = arr.join("");
    for (let i = 0; i < arr_.length; i++) {
        if (arr_[i] === "-")
            n++;
    }

    while (n) {
        arr_ = arr_.replace('-', '1'); 
        n--;
    }
   
    if (Number.isNaN(Number(arr_)))
        return "Wrong input";

    let res = 0;

    for (let i = 0; i < arr.length; i++)
        res += arr[i];

    return res;
}

module.exports = { summ };